mapper.select:
		select * from release_news


				and `website` like concat('%',#{website},'%')


				and `news_title` like concat('%',#{newsTitle},'%')




                order by ${sidx} ${order}


                order by id desc



			limit #{offset}, #{limit}

mapper.delete:
DELETE FROM release_news WHERE news_url in

	#{item.newsUrl}

AND 

	#{item.webstie}
